                    />
                </div>
                <div id="main">
                    <div class="invite-index">
                        <div class="tangwei big-image" style="height: 490px;"></div>
                        <div class="share-content">
                            <div class="bd">
                                <div class="mobile">
                                    <input
                                        id="mobile"
                                        class="border-box gray"
                                        placeholder="请输入手机号"
                                        maxlength="11"
                                        v-model="number"/>
                                </div>
                                <div class="submit border-box" @click="userGetCoupon(index)">免费领取</div>
                            </div>
                            <div class="ft">
                                <ul class="coffee-list">
                                    <li class="item" id="item-1"></li>
