</template>

<script>
    export default {
        data() {
            return {
                couponList: [],
                index: 1,
                number: ''
            }
        },
        methods: {
            getCouponList() {
                this.$api.couponList({}, res => {
                    if (res.status) {
                        this.couponList = res.data
                    }
                })
            },
            userGetCoupon(index) {
                this.$api.marketingGetCoupon({promotion_id: this.couponList[index].id,mobile:this.number}, res => {
                    if (res.status) {
                        this.$dialog.toast({mes: res.msg, timeout: 1300})
                    }
                })
            }
        },
        created() {
            this.getCouponList()//先去拉数据
        },
    }
</script>

